def calculate_grade(scores):
    total = sum(scores)
    average = total / len(scores)

    if average &gt;= 90:
        return 'A'
    elif average &gt;= 80:
        return 'B'
    elif average &gt;= 70:
        return 'C'
    elif average &gt;= 60:
        return 'D'
    else:
        return 'F'
